import React, { useState } from 'react';
import WORDSET_DATA from './data/word-list.json';
import TAGS_DATA from './data/tags.json';
import Dropdown from 'react-bootstrap/Dropdown';

function Filter(props) {
    const [isFilterOpen, setIsFilterOpen] = useState(false);

    const toggleFilter = () => {
        setIsFilterOpen(!isFilterOpen);
    };

    const handleCheckboxChange = (tag) => {
        // Handle checkbox change logic here
        console.log(`Checkbox for ${tag} clicked`);
    };

    const tagArray = TAGS_DATA.map((tagObj) => (
        <Dropdown.Item key={tagObj.id}>
            <input
                type="checkbox"
                id={tagObj.id}
                onChange={() => handleCheckboxChange(tagObj.word)}
            />
            <p htmlFor={tagObj.id}>{tagObj.word}</p>
        </Dropdown.Item>
    ));

    return (
        <form>
            <h2>Start your adventures from:</h2>
            <Dropdown show={isFilterOpen} onClick={() => {}}>
                <Dropdown.Toggle variant="success" id="dropdown-basic" onClick={toggleFilter}>
                    <span className="material-icons filter-icon" aria-hidden="true">
                        filter_alt
                    </span>
                    Filter Tags
                </Dropdown.Toggle>
                <Dropdown.Menu>{tagArray}</Dropdown.Menu>
            </Dropdown>
        </form>
    );
}

export default function SearchFilter(props) {
    const cardArray = WORDSET_DATA.map((listObj) => (
        <div className="card" key={listObj.id}>
            <div className="card-body">
                <p>{listObj.title}</p>
            </div>
        </div>
    ));

    return (
        <div className="searchfilter">
            <Filter />
            <div className="row-cols-8">{cardArray}</div>
        </div>
    );
}
