import React, { useState } from 'react';
import WORDSET_DATA from './data/word-list.json';
import TAGS_DATA from './data/tags.json';
import Dropdown from 'react-bootstrap/Dropdown';

export default function SearchFilter(props) {
    const [isFilterOpen, setIsFilterOpen] = useState(false);
    const [selectedTags, setSelectedTags] = useState([]);
    const [filteredWords, setFilteredWords] = useState([]);

    const toggleFilter = () => {
        setIsFilterOpen(!isFilterOpen);
    };

    const handleTagChange = (tag) => {
        const updatedTags = selectedTags.includes(tag)
            ? selectedTags.filter((selectedTag) => selectedTag !== tag)
            : [...selectedTags, tag];

        setSelectedTags(updatedTags);

        // Update filtered words based on selected tags
        const filteredData = WORDSET_DATA.filter((dataObj) =>
            updatedTags.length === 0 || updatedTags.some((tag) => dataObj.tags.includes(tag))
        );
        const words = filteredData.flatMap((dataObj) => dataObj.words);
        setFilteredWords(words);
        props.applyFilterCallback(updatedTags);
    };

    const handleCloseDropdown = () => {
        setIsFilterOpen(false);
    };

    const handleClick = (event) => {
        event.preventDefault();
        // Perform any additional logic if needed
        // This function can be left empty if not required for the submit action.
    };

    return (
        <form onSubmit={handleClick}>
            <div className="searchfilter">
                <h2>Start your adventures from:</h2>
                <div className="bars">
                    {/* ... (previous code) */}
                    <div>
                        <h3>Related Words:</h3>
                        {WORDSET_DATA.map((dataObj) => {
                            // Filter words based on selected tags
                            const filteredData = selectedTags.length > 0
                                ? dataObj.words.filter((word) => selectedTags.some((tag) => dataObj.tags.includes(tag)))
                                : dataObj.words;

                            return (
                                <div key={dataObj.id} className="row-cols-8">
                                    <div className="card">
                                        <div className="card-body">
                                            <h5 className="card-title">
                                                {selectedTags.length > 0 ? `Selected Tags: ${selectedTags.join(', ')}` : 'All Tags'}
                                            </h5>
                                            <ul>
                                                {filteredData.map((word, index) => (
                                                    <li key={index}>{word}</li>
                                                ))}
                                            </ul>
                                        </div>
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                </div>
            </div>
        </form>
    );
}
