import React, { useState } from 'react';

export function FlipCard(props) {

    const [flip, setFlip] = useState(false);

    const handleFlip = () => {
        setFlip(!flip);
    }

    return (
    <>
        <h1>Flip Cards!</h1>
        <div className="card" onClick={handleFlip}>
            <img src="./img/apple.png" alt=" an apple" />
        </div>

        <div className="quiz-next">
            <button>&#8592;</button>
            <button>&#8594;</button>
        </div>
    </>
    )
}
